insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'Animal Farm','literatura piękna','A farm is taken over by its overworked, mistreated animals. With flaming idealism and stirring slogans, they set out to create a paradise of progress, justice, and equality.','https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1325861570l/170448.jpg',2)
insert into book (amount,title, genre, description, img,publishing_house_id) values (3,'Malowany Człowiek','fantasy','Zaszczuta i zdziesiątkowana ludzkość przeklina noc. Z każdym zmierzchem, w oparach mgły, nadchodzą opętane żądzą mordu bestie. Przerażeni ludzie chronią się za magicznymi runami. Usiłują wymodlić dla siebie i najbliższych kolejny dzień życia. Rzeź ustaje bladym świtem, gdy światło zapędza demony z powrotem w Otchłań.', 'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1631005832l/58910483._SY475_.jpg',3)
insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'W pogoni za słońcem','literatura popularnonaukowa','Naszą biologię zaprogramowało Słońce. Dostęp do jego światła stanowi kluczowy warunek szczęśliwego i satysfakcjonującego życia. To właśnie światło słoneczne wpływa na cykle snu i czuwania, układ odpornościowy i zdrowie psychiczne.','https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1561408924l/49523850._SX318_SY475_.jpg',7)
insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'Nieznośna lekkość bytu','literatura piękna',' W tej pięknej i czułej opowieści o praskim lekarzu i jego kochankach zbiegają się rozmaite kwestie, do których wielki czeski pisarz nieustannie powracał. Najważniejsza to przypadek jako czynnik, który determinuje ludzkie losy. Miłość jest przypadkiem. To przypadek decyduje, że zakochujemy się właśnie w tej, a nie w innej osobie. Przypadek decyduje również o tym, czym zajmujemy się przez całe życie, kim jesteśmy, z jakimi ludźmi się stykamy. A w państwie totalitarnym czynnik losowy staje się tym bardziej paradoksalny, im bardziej wszechwładny ustrój stara się go wyeliminować i narzucić obywatelom sztuczne ograniczenia.','https://ecsmedia.pl/c/nieznosna-lekkosc-bytu-b-iext97226579.jpg',6)
insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'Żart','literatura piękna','To właśnie przydarza się Ludvikowi, głównemu bohaterowi opowieści, lekarzowi w komunistycznej Czechosłowacji, gdzie system nie pozwala na śmiech i rozrywkę. Zwłaszcza jeśli ma znaczenie polityczne i może obrażać Karola Marksa, ojca socjalizmu. Z powodu niewinnego żartu Ludvik zostanie wyrzucony z pracy. W dyktaturze taka degradacja niesie ze sobą również degradację społeczną i polityczną, która sprawia, że człowiek staje się nikim.','https://ecsmedia.pl/c/zart-w-iext82806488.jpg',6)
insert into book (amount,title, genre, description, img,publishing_house_id) values (1,'Zdradzone testamenty','literatura piękna','Milan Kundera w dziewięciu esejach dzieli się tym, co w książkach jest dla niego najistotniejsze. Błyskotliwie, dowcipnie i zaskakująco analizuje najwybitniejsze dzieła światowej literatury, a jego wnioski często stoją w sprzeczności z opinią uznanych krytyków.','https://s.lubimyczytac.pl/upload/books/246000/246946/577579-170x243.jpg',6)
insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'Król darknetu. Polowanie na genialnego cyberprzestępcę', 'reportaż', 'Świat wirtualny, w którym toczy się codzienne cyberżycie, ma też swoją ciemną odsłonę. Darknet. To właśnie tam, po drugiej stronie internetowego lustra, Ross Ulbricht postanowił zrealizować swój zuchwały plan, który miał doprowadzić do narkotykowej rewolucji.', 'https://s.lubimyczytac.pl/upload/books/4910000/4910992/798810-170x243.jpg',7)
insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'Przyszło nam tu żyć. Reportaże z Rosji', 'reportaż', 'W cieniu wielkiej polityki, wojen, fortun zdobywanych z dnia na dzień, z daleka od obiektywów kamer i świateł reflektorów jest inna Rosja. To kraj, w którym w opuszczonym moskiewskim szpitalu koczuje grupa nastolatków – stalkerów, diggerów, samobójców, strażników i upiorów. W walącym się hotelu robotniczym mieszka kilkanaście rodzin, które od lat czekają na lokale zastępcze – pisali w tej sprawie już nawet do papieża', 'https://s.lubimyczytac.pl/upload/books/4904000/4904847/784401-170x243.jpg',8)
insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'Baśnie braci Grimm', 'klasyka', 'Baśnie braci Grimm (niem. Kinder- und Hausmärchen) – zbiór baśni spisanych przez niemieckich pisarzy i językoznawców, braci Wilhelma i Jacoba Grimmów i opublikowany w roku 1812 (tom I) i w roku 1815 (tom II). Zbiór ten, powstały na podstawie wieloletnich badań podań, mitów i opowieści ludowych, stał się klasyką literatury, przetłumaczoną na wiele języków.', 'https://woblink.com/image-cache/sellable_cover/products/images/5e8147388293d.jpeg',6)
insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'Przemilczane', 'literatura piękna', 'Annemarie, która przybywa z głębi Rzeszy na opolską wieś, by w ramach Reichsarbeitsdienst pełnić służbę w gospodarstwie Stanoschów i Paul, który, oczekując na wezwanie do Wehrmachtu, zmaga się z rodzinnymi problemami przerastającymi jego młodzieńczy umysł. Oboje są młodzi, oboje są świadkami takich wydarzeń i oboje woleliby nie wiedzieć i nie widzieć.', 'https://s.lubimyczytac.pl/upload/books/4994000/4994506/946144-170x243.jpg',7)
insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'Prowadź swój pług przez kości umarłych', 'literatura piękna', 'Inspirująca, głośno dyskutowana powieść. Thriller moralny z metafizyczną zagadką, zaskakującą pod każdym względem. Zimą piękna Kotlina Kłodzka jest miejscem mało przyjaznym i opustoszałym. Wsród nielicznych mieszkańców pozostała Janina Duszejko - miłośniczka astrologii, która w wolnych chwilach dogląda domów nieobecnych sąsiadów i tłumaczy poezję Blake''a. Niespodziewanie przez tę spokojną okolicę przetacza się fala morderstw, których ofiarami padają myśliwi. Czy to możliwe, by - jak sugeruje Duszejko - to zwierzęta zaczęły czyhać na życie swoich oprawców? Wszak Blake pisał: "Błąkająca się Sarna po lesie / Ludzkiej Duszy niepokój niesie" ...', 'https://s.lubimyczytac.pl/upload/books/4993000/4993181/943762-170x243.jpg',8)
insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'Lalka i perła', 'literatura piękna', 'Ta książka stworzyła Lalkę na nowo. Olga Tokarczuk zaprasza bohaterów powieści Prusa na prywatną rozmowę. Słucha ich z uwagą i empatią, ale też niepokojem. Ich fascynujące historie niejednokrotnie rymują się z opowieściami naszej współczesności, a oni sami okazują się zaskakująco do nas podobni.', 'https://s.lubimyczytac.pl/upload/books/4958000/4958162/880914-170x243.jpg',8)
insert into book (amount,title, genre, description, img,publishing_house_id) values (3,'E.E.', 'literatura piękna', ' "E.E." jest (...), jak pierwsza powieść Tokarczuk, przypowieścią o tajemnicy ludzkiej osobowości i poszukiwaniu sensu życia, świata. Autorka, mimo fantastycznego kostiumu, okultystycznego, gnostycznego, teozoficznego etc. sztafażu, jest pisarką reprezentującą psychologiczny realizm i sceptycyzm poznawczy. Pisze w możliwie atrakcyjny sposób o rzeczach poważnych i w poważnym tonie. Zwróćmy uwagę, że w jej twórczości w ogóle nie ma ironii, nawet bardziej wyrazistego humoru. Jest chłodna wiwisekcja zdarzeń, życia i osobowości bohaterów, wielostronny ogląd zjawiska i towarzyszących mu okoliczności.Ciekawie jest obserwować jak Tokarczuk zadbała o uatrakcyjnienie swojej powieści, o "wpasowanie" jej w aktualne dyskusje krytyczne i gusty czytelnicze. Jest w "E.E." wątek dziś tak popularny, jak dojrzewanie małych dziewczynek, są tropy feministyczne, jest warstwa "New Age-owska", sięganie do problematyki pogranicza kultur i epok, jest "klasyczna" tradycja powieściowa. Nawet tytuł może być traktowany jako mrugniecie do czytelnika, w którego życiorysie zapisał się skrót "E.T.". Wszystko to wzbudza zaciekawienie, ale w ostatecznym rezultacie jest sprawą drugorzędną.Prawdziwą wartością, jaką ma do zaproponowania autorka "E.E.", jest przywrócenie zainteresowania osobą, jednostką ludzką, w pełni jej duchowych wymiarów i nie dezaktualizującej się tajemniczości. Jest to rodzaj indywidualizmu na miarę epoki, która się otworzyła i nie ma on wiele wspólnego z tzw. indywidualizmem Polaków, jakim dotąd się szczyciliśmy."Mirosław Ratajczak, "Odra", 1996 nr 2', 'https://s.lubimyczytac.pl/upload/books/4945000/4945500/854736-170x243.jpg',8)
insert into book (amount,title, genre, description, img,publishing_house_id) values (3,'Śmierć pięknych saren', 'literatura piękna', 'Za co tak bardzo kochamy Otę Pavla? Co sprawia, że kilka dekad po jego przedwczesnej śmierci wciąż sięgamy po jego książki? Z pewnością są to zawarte w tych tekstach ponadczasowe wartości: miłość, przyjaźń, rodzina i przyroda, a także podszyte delikatnym humorem nostalgia i bezradność wobec przemijającego świata i czasu. Lecz najważniejsza jest być może ta, tak charakterystyczna dla Pavla, magia życia, nieuchwytna, utkana ze słów i przypominająca, że każdy nasz dzień, nawet nie najpiękniejszy, ma olbrzymią wartość, którą jednak musimy odnaleźć przede wszystkim w naszych sercach.', 'https://s.lubimyczytac.pl/upload/books/4986000/4986626/932568-170x243.jpg',8)
insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'Diuna', 'science fiction', 'Arrakis, zwana Diuną, to jedyne we wszechświecie źródło melanżu. Z rozkazu Padyszacha Imperatora planetę przejmują Atrydzi, zaciekli wrogowie władających nią dotychczas Harkonnenów. Zwycięstwo księcia Leto Atrydy jest jednak pozorne – przejęcie planety ukartowano. W odpowiedzi na atak Imperium i Harkonnenów dziedzic rodu Atrydów Paul staje na czele rdzennych mieszkańców Diuny i sięga po imperialny tron.', 'https://s.lubimyczytac.pl/upload/books/4923000/4923655/821301-352x500.jpg',4)
insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'Mesjasz Diuny', 'science fiction', 'Minęło kilkanaście lat, odkąd Fremeni pod dowództwem Paula Muad''Diba pokonali połączone siły Harkonnenów i imperialnych sardaukarów. Paul poślubił księżniczkę Irulanę i zasiadł na tronie Imperium. Pustynna Arrakis, zwana Diuną, jest stolicą wszechświata, a Imperator Paul Atryda wydaje z niej poprzedzone proroczymi wizjami rozkazy. Tymczasem stare ośrodki władzy - Bene Gesserit, Gildia Kosmiczna i Bene Tleilax - zawiązują spisek przeciw nowemu Imperatorowi.', 'https://s.lubimyczytac.pl/upload/books/8000/8601/455810-352x500.jpg',4)
insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'Dzieci Diuny', 'science fiction', 'Upłynęło dziewięć lat, odkąd oślepiony Muad’Dib ruszył samotnie na pustynię na spotkanie z Szej-huludem. W tym czasie Imperium rządziła jego siostra Alia. Na drodze opętanej regentce wciąż jednak stoją prawowici następcy Paula – Leto i Ganima. Dzieci są też zagrożeniem dla rodu Corrinów, odwiecznego wroga Atrydów, chcącego ponownie zasiąść na Tronie Złotego Lwa. Bliźnięta, którym również grozi opętanie, podejmują dramatyczną walkę na kilku frontach. Tymczasem na Arrakis pojawia się charyzmatyczny niewidomy Kaznodzieja, który bardzo przypomina ich zmarłego ojca...', 'https://s.lubimyczytac.pl/upload/books/4925000/4925908/812672-352x500.jpg',4)
insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'Pytania z kosmosu', 'literatura popularnonaukowa', 'Światowej sławy astrofizyk i kolejna olśniewająca książka o złożoności życia i wszechświata.Sondy kosmiczne wyprawiają się coraz dalej, akceleratory rozbijają atomy, a radioteleskopy i teleskopy rejestrują promieniowanie wyemitowane przed miliardami lat… Nasze rozumienie Wszechświata i miejsca, jakie w nim zajmujemy, zmienia się w rewolucyjny sposób. Neil DeGrasse Tyson w intrygujący sposób przymierza najnowsze koncepcje astrofizyczne do odwiecznych pytań filozoficznych: „skąd się wzięliśmy?”, „czy jesteśmy sami?”, „skąd wiemy to, co wiemy?”.Autor – nawiązujący do wiedzy dawnych mędrców, od Eratostenesa po Galileusza, i objaśniający najważniejsze naukowe osiągnięcia teraźniejszości, takie jak odkrycie egzoplanet oraz identyfikowanie cząstek elementarnych – zabiera czytelnika w podróż z przeszłości w przyszłość, od niewyobrażalnie małego do przeogromnego, od granic obecnej wiedzy ku nieznanemu. Dowcipny jak zawsze, zachęca do nieustannego patrzenia w górę i poszukiwania…', 'https://s.lubimyczytac.pl/upload/books/4970000/4970120/903566-352x500.jpg',1)
insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'Kosmiczne rozterki', 'literatura popularnonaukowa', 'Kosmiczne rozterki to już trzecia – po Astrofizyce dla zabieganych i Kosmicznych zachwytach – popularnonaukowa książka Neila deGrasse’a Tysona, w której ten ulubiony przez polskich widzów i czytelników astrofizyk zabiera nas w niesamowitą podróż.Pełne pasji, porównań, obrazowych zestawień i intrygujących anegdot opowieści Neila o wszechświecie, tym bliskim i tym dalekim, a także o historii jego odkrywania naprawdę potrafią rozbudzić wyobraźnię i sprawić, że za każdym razem, gdy spojrzymy w migoczące milionami gwiazd noce niebo, poczujemy dreszcz emocji i zachwyt pięknem kosmosu.W Kosmicznych rozterkach Neil deGrasse Tyson porusza mnóstwo kosmicznych tematów: tych znanych z pierwszych stron gazet i tych mniej popularnych, a nie mniej – a może nawet bardziej – ciekawych.Czarne dziury, promieniowanie gwiazd, woda w przestrzeni kosmicznej, chaos w Układzie Słonecznym, kwazary jako silniki galaktyk – to tylko niektóre z nich. Jak możemy uniknąć zderzenia z asteroidą?Jak może wyglądać kosmiczny koniec naszego świata? Dlaczego śmierć w czarnej dziurze jest najbardziej spektakularna? Oto próbka kosmicznych rozterek, z jakimi przyjdzie nam się zmierzyć w nowej książce Neila.Kosmiczne rozterki to nie tylko opowieści o kosmosie.To także inspirująca lektura o znaczeniu i sensie życia, o wpływie nauki na naszą kulturę, a także o zderzeniu nauki z religią.', 'https://s.lubimyczytac.pl/upload/books/4904000/4904148/770310-352x500.jpg',1)
insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'Kosmiczne zachwyty', 'literatura popularnonaukowa', 'Czy można badać kosmos zwykłym kijem? Jaki kolor ma wszechświat? Czy stojąc na szczycie Mount Everestu, jesteśmy najdalej od środka Ziemi? Dlaczego wydostanie się z wnętrza Słońca zajmuje światłu aż 5000 lat? Czy w pasie asteroid faktycznie tak łatwo zaliczyć pozaziemską stłuczkę?Na te i wiele innych rozpalających wyobraźnię kosmicznych pytań odpowiada w swojej książce słynny amerykański astrofizyk i znakomity popularyzator nauki Neil deGrasse Tyson.Entuzjazm i pasja, z jaką Tyson opisuje wszechświat – ten bliski i ten odległy – udziela się milionom jego widzów i czytelników. Nawet najbardziej skomplikowane kosmiczne zjawiska stają się w jego ujęciu zrozumiałe i, co najważniejsze, fascynujące! Lektura Kosmicznych zachwytów – napisanej lekkim piórem książki pełnej zdumiewających faktów i wciągających opowieści – sprawi, że nocne wpatrywanie się w rozgwieżdżone niebo już nigdy nie będzie takie samo.', 'https://s.lubimyczytac.pl/upload/books/4865000/4865831/695906-352x500.jpg',1)
insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'Błękitna kropka. Człowiek i jego przyszłość w kosmosie', 'literatura popularnonaukowa', 'W „Kosmosie” Carl Sagan tylko „rzucił okiem” na wspaniałą tajemnicę Wszechświata i udostępnił ją milionom ludzi na całym świecie. W „Błękitnej kropce”, oszałamiającej kilkuset barwnymi ilustracjami kontynuacji tamtego bestsellera, słynny astronom kończy swoją rewolucyjną podróż przez przestrzeń i czas. Przyszłe pokolenia będą spoglądać wstecz na naszą epokę jako czas, w którym ludzka rasa wreszcie w radykalny sposób pokonała nową granicę – kosmiczną przestrzeń. W swojej książce Sagan śledzi romantyczną historię naszego startu w kosmos i ocenia przyszłość, która otworzy się przed nami, kiedy już skolonizujemy nasz system planetarny i wyruszymy dalej, do odległych galaktyk. Autor niezwykle sugestywnie udowadnia, że eksploracja i osiedlenie się człowieka na innych światach, w tym także zaludnienie nieznanych jeszcze galaktyk, nie jest ani fantazją, ani luksusem, ale raczej koniecznym warunkiem przetrwania ludzkiego rodzaju. „Fascynująca... Zapadająca w pamięć... Odkrywcza... Być może najlepsza z książek Sagana!”. – The Washington Post', 'https://s.lubimyczytac.pl/upload/books/4846000/4846042/658282-352x500.jpg',2)
insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'Siedem wieków śmierci', 'literatura popularnonaukowa', '„Choć to książka o śmierci, zabieram w niej czytelników w podróż przez życie…” Richard Shepherd – wybitny brytyjski patolog – całe życie spędził blisko zmarłych. Każda przeprowadzona przez niego autopsja to unikalne śledztwo, które nie tylko pozwala odkryć tajemnicę śmierci badanej osoby, ale również poznać, z czym się zmagała i czego doświadczała na różnych etapach życia. Ofiary morderstw i nieszczęśliwych wypadków, zmarli w wyniku choroby – każde z ciał, czy to starca, czy nastolatka, ma coś do przekazania: o rozwoju człowieka, o zagrożeniach, na jakie narażeni jesteśmy od niemowlęctwa po starość, o naszych nawykach, niechęciach i upodobaniach. Przedstawiając dwadzieścia cztery najbardziej intrygujące, pouczające i nigdy wcześniej nieopowiedziane przypadki, które obejmują siedem wieków ludzkiej egzystencji, doktor Shepherd dzieli się autopsjami, które nauczyły go tyle samo o nieuchronności śmierci, co o cudzie życia. Nowa książka autora bestsellerowych Niewyjaśnionych okoliczności to przepiękna, poruszająca, a przede wszystkim kojąca opowieść o życiu i śmierci.', 'https://s.lubimyczytac.pl/upload/books/4990000/4990128/938532-352x500.jpg',6)
insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'Czarny łabędź. Jak nieprzewidywalne zdarzenia rządzą naszym życiem', 'literatura popularnonaukowa', 'Co łączy wynalazek koła, zagładę Pompejów, krach na Wall Street, Harry''ego Pottera i Internet? Dlaczego wszyscy prognostycy są oszustami? Co miłośnicy Katarzyny Wielkiej mogą nam powiedzieć o prawdopodobieństwie? Dlaczego nigdy nie powinieneś gonić ruszającego pociągu ani wierzyć codziennym gazetom? Bestsellerowa książka Nassima Nicholasa Taleba analizuje zjawisko Czarnych Łabędzi: niespodziewanych wydarzeń, które spadają na nas nagle i odciskają ogromne piętno na naszym życiu. Czy to będzie wybuch I wojny światowej, dojście Hitlera do władzy, zamachy z 11 września 2001 roku w Nowym Jorku, kryzys finansowy 2008 roku czy aktualna pandemia koronawirusa Covid-19, wydarzenia te całkowicie zmieniają podstawy naszego życia. Ich wpływ na los świata jest ogromny, ale są niemożliwe do przewidzenia, dopiero po ich wystąpieniu staramy się je zracjonalizować. "Połączenie niskiej przewidywalności i znacznych skutków dla otaczającej rzeczywistości czyni Czarnego Łabędzia wielką zagadką" - pisze Nassim Nicholas Taleb. I proponuje czytelnikowi rozwiązanie tej zagadki. Jego książka to niezmiernie ważna lektura dla wszystkich, którzy chcą żyć świadomie w dzisiejszym, pełnym niepewności i niebezpieczeństw, szybko zmieniającym się świecie.', 'https://s.lubimyczytac.pl/upload/books/4921000/4921407/802240-352x500.jpg',6)
insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'Zbrodnia i kara', 'klasyka', 'Słynna powieść Fiodora Dostojewskiego, opowiadająca o losach byłego studenta Rodiona Raskolnikowa, który postanawia zamordować i obrabować bogatą lichwiarkę. Pomysł na tę powieść narodził się w czasie, kiedy sam autor przebywał na katordze. Zainteresował się wtedy psychologią współwięźniów, wśród których byli i tacy, którzy zostali skazani za morderstwo. Bohater powieści, 23-letni były student prawa jest półsierotą, ma jednak kochająca rodzinę. Jego matka i siostra darzą go głęboką miłością i wspierają finansowo. Jednak bohater, zbuntowany przeciw porządkowi świata, decyduje się popełnić morderstwo. Jest przekonany, że jako jednostka wybitna, ma prawo zabijać, gdyż geniusz usprawiedliwia wszystkie zbrodnie na „zwykłych” ludziach. Morderstwo ma stać się rodzajem sprawdzianu jego odwagi. Choć jednocześnie bezpośrednia pobudką tego czynu jest zła sytuacja finansowa Raskolnikowa…', 'https://s.lubimyczytac.pl/upload/books/4989000/4989043/936619-352x500.jpg',2)
insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'Profesor', 'klasyka', 'William Cromsworth odrzuca swe arystokratyczne dziedzictwo i wyrusza do Brukseli, by tam znaleźć szczęście. Zostaje nauczycielem języka angielskiego w szkole z internatem dla młodych panien, gdzie musi stawić czoła manipulacjom ze strony dyrektorki, Zoraïde Reuter, która, powodowana zazdrością, usiłuje zniszczyć miłość rodzącą się między nim a uczennicą, Frances Henri, równie jak William ambitną i energiczną młodą kobietą. Niezwykła opowieść o miłości a zarazem krytyka relacji damsko-męskich, które w epoce wiktoriańskiej niejednokrotnie sprowadzały się do walki o dominację. W książce tej znajdziemy wątki autobiograficzne. Charlotte Brontë, podobnie jak jej bohater William, uczyła angielskiego w szkole w Brukseli. I podobnie jak on doświadczała miłości. Jednak jej obiektem był żonaty właściciel szkoły, co nie mogło zakończyć się happy endem.', 'https://s.lubimyczytac.pl/upload/books/4991000/4991460/940971-352x500.jpg',2)
insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'Dziwne Losy Jane Eyre', 'klasyka', 'Historia młodej dziewczyny, która po stracie obojga rodziców, trafia do domu brata swojej matki. Nie potrafi jednak obudzić uczuć u ciotki, która – gdy tylko nadarza się okazja – pozbywa się dziewczynki, wysyłając ją do szkoły dla sierot, słynącej z surowego rygoru. Jane jednak udaje się przeżyć, zdobywa wykształcenie i wreszcie znajduje pracę jako guwernantka, w domu Edwarda Rochestera, samotnie wychowującego przysposobioną córkę. Wydawałoby się, że tu wreszcie znajdzie prawdziwe szczęście. Jednak los upomni się zadośćuczynienia za winy z przeszłości jej ukochanego pana. Jane nocą ucieka szukać swojej własnej drogi… Jane Eyre to powieść, o której wnet po jej opublikowaniu gazety angielskie pisały codziennie, do tego – niemal w samych superlatywach, a taki publiczny zachwyt nie zdarza się często. Książka Charlotte Brontë porwała tłumy i nadal pozostaje powieścią kultową. W czym tkwi jej fenomen? Wydaje się, że w wierności samemu życiu. Bez względu na epokę samotność, tęsknota i cierpienie są zawsze te same i zawsze tak samo przeżywane. Zwłaszcza jeśli jest to samotność wśród ludzi, tęsknota za zwykłym ciepłem drugiego człowieka i cierpienie wynikające z odtrącenia przez innych. Czy działo się to dawno temu, czy działo się wczoraj – trauma odrzucenia pozostaje ta sama, a lekcja miłości do odrobienia. Jane Eyre ukazała tę drogę nie tylko jako możliwą do przezwyciężenia, ale pewną, przykładem własnej osoby ręcząc, że sprawiedliwość istnieje, a cierpienie zostaje wynagrodzone.', 'https://s.lubimyczytac.pl/upload/books/4968000/4968674/901002-352x500.jpg',2)
insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'Anna Karenina', 'klasyka', 'Anna Karenina wiedzie dostatnie, stabilne i nieco nudne życie u boku dużo starszego męża. Piękna młoda kobieta spełnia się jako matka, ale nie stroni też od przyjęć, jest uwielbiana na petersburskich salonach. Spokój znika wraz z pojawieniem się hrabiego Wrońskiego, który wprowadza w życie Anny namiętność, jakiej nigdy nie zaznała. I zniszczenie, którego nie da się cofnąć. To ponadczasowa powieść o uczuciu, które od początku było skazane na potępienie. Uczuciu, dla którego Anna poświęciła wszystko. Powieść Tołstoja po raz kolejny inspiruje Hollywood. Reżyser Joe Wright tworzy pełen rozmachu pejzaż rosyjskich elit. Keira Knightley jako Anna Karenina i Jude Law w roli jej męża – megagwiazdorska obsada pozwala na nowo odkryć najpiękniejszą rosyjską historię miłosną.', 'https://s.lubimyczytac.pl/upload/books/152000/152298/783829-352x500.jpg',2)
insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'Przekłady dla teatru ', 'dramat', 'Paweł Hertz (1918–2001) – pisarz, poeta, tłumacz i wydawca, przez wiele lat związany z Państwowym Instytutem Wydawniczym – jedna z najważniejszych postaci polskiej kultury i humanistyki XX wieku. Autor przeszło dziesięciu tomów poetyckich i ponad 15 eseistycznych. Tłumaczył m.in. twórczość Lwa Tołstoja, Iwana Turgieniewa, Anatola France’a, Marcela Prousta, Jacoba Burckhardta, Hugo von Hofmannsthala; redakcyjnie opracowywał dzieła wybrane m.in. Aleksandra Puszkina, Fiodora Dostojewskiego, Zygmunta Kraińskiego, czy Juliusza Słowackiego. W czwartym tomie serii poświęconej twórczości Pawła Hertza przybliżamy dwie sztuki – Adrianne Lecouvreur Ernesta Legouve’a i Eugene Scribe’a oraz Miesiąc na wsi Iwana Turgieniewa – w doskonałym przekładzie Hertza. Tom ten uzupełnia wnikliwe posłowie Wojciecha Majcherka o złożonych, czy wręcz „burzliwych” relacjach Hertza z teatrem.', 'https://s.lubimyczytac.pl/upload/books/4987000/4987404/933881-352x500.jpg',5)
insert into book (amount,title, genre, description, img,publishing_house_id) values (1,'Romeo i Julia ', 'dramat', 'Źródeł utworu należy doszukiwać się we włoskiej opowieści romantycznej Mattea Bandella (Le tre parti de le Novelle del Bandello,1554 r.). Dramat przedstawia historię tragicznej miłości dwojga młodych ludzi, którzy do czasów obecnych stali się wzorcami romantycznych kochanków. Akcja toczy się w Weronie i Mantui i rozgrywa się w ciągu pięciu dni: od niedzielnego poranka do piątkowego świtu w Weronie. W IV akcie akcja na krótko przenosi się do Mantui, gdzie Romeo dowiaduje się o rzekomej śmierci ukochanej. Sztuka przedstawia konflikt dwóch zwaśnionych rodzin, których młodociane dzieci zakochują się w sobie. Na skutek perypetii i przeszkód rodzinnych, Julia wypija otrzymaną od ojca Laurentego miksturę, która wprawia ją w stan podobny do śmierci. Nieświadomy intrygi i zrozpaczony Romeo podejmuje decyzję o samobójstwie. Dochodzi do przebudzenia Julii, która widząc śmierć Romea, postanawia także zakończyć swoje życie. Śmierć dzieci, zwaśnionych Montecchich i Capuletich, prowadzi w końcu do pojednania między rodami.', 'https://s.lubimyczytac.pl/upload/books/4974000/4974466/911920-352x500.jpg',5)
insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'Cudze meble ', 'dramat', 'W "Cudzych meblach" została przedstawiona historia rodzinna i psychologiczna, uwzględniająca współczesną polską historię jak czasy PRL-u, emigracji i rozbicie rodziny w skutek zmian społecznych. W dramacie występują miedzy innymi trzy pokolenia silnych kobiet: babcia-matka-córka. Między bohaterami dochodzi do konfrontacji osobistych cierpień i eskalacji rodzinnych konfliktów i traum narodowych. Cudze meble to debiut dramatyczny Magdaleny Miecznickiej, kontynuujący najlepszą tradycję sztuk teatralnych Tadeusza Różewicza czy Sławomira Mrożka. Walory poetyckie sztuki stanowią o jej artystycznym i głęboko ludzkim wymiarze.', 'https://s.lubimyczytac.pl/upload/books/309000/309740/513294-352x500.jpg',5)
insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'Policja ', 'dramat', 'Debiut sceniczny dwudziestoośmioletniego pisarza, wystawiony na scenie Teatru Dramatycznego w 1958 r. Choć autor we wstępie napisał: „Sztuka ta nie zawiera niczego poza tym, co zawiera, to znaczy nie jest żadną aluzją do niczego, nie jest też żadną metaforą i nie trzeba jej odczytywać.”, publiczność wiedziała swoje i odczytała opowieść o państwie tak idealnym, że nie potrzebna jest już w nim policja jak najbardziej aluzyjnie, a Sławomir Mrożek odniósł swój pierwszy sceniczny sukces.  „Każdemu neurotykowi, który stale się trapi swoim nędznym losem, polecam napisanie sztuki i wystawienie jej w teatrze” – ironizował później w „Baltazarze”.', 'https://s.lubimyczytac.pl/upload/books/4515000/4515199/572770-352x500.jpg',5)
insert into book (amount,title, genre, description, img,publishing_house_id) values (3,'Polska odwraca oczy', 'reportaż', 'W zbiorze znajduje się reportaż o dręczonych nieletnich pacjentach szpitala psychiatrycznego w Starogardzie Gdańskim („Oddział chorych ze strachu”), tuszowaniu przez policję najtrudniejszych spraw związanych z zabójstwami („Ten trup się nie liczy”) oraz – nigdy niepublikowana – rozmowa z żoną Mariusza Trynkiewicza osadzonego w ośrodku specjalnym w Gostyninie, zabójcy czterech chłopców. Z reportaży Kopińskiej wyłania się obraz nieskutecznego wymiaru sprawiedliwości i ludzi pozostawionych z poczuciem krzywdy. Po publikacji jej tekstów przestępcy trafiali do więzienia, a w prawie wprowadzano zmiany dotyczące bezpieczeństwa w instytucjach zamkniętych dla dzieci.', 'https://s.lubimyczytac.pl/upload/books/4629000/4629944/615273-352x500.jpg',5)
insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'Zdązyć przed Panem Bogiem ', 'reportaż', 'Od ponad czterdziestu lat Zdążyć przed Panem Bogiem pozostaje klasykiem polskiego reportażu i jedną z najbardziej przejmujących opowieści o powstaniu w getcie warszawskim w kwietniu 1943 roku i losach polskich Żydów, jakie stworzyła literatura XX wieku. To książka, która powstała z rozmów z Markiem Edelmanem, lekarzem, podczas wojny jednym z przywódców powstania. Marek Edelman opowiada o wojnie i o medycynie. O tym, czym jest godna śmierć i godne życie. Jest to także opowieść o ludziach, którzy mu towarzyszyli – o powstańcach, zakonnicach, dawnych akowcach, kobietach, które kochał, o pacjentach i o lekarzach.', 'https://s.lubimyczytac.pl/upload/books/4859000/4859145/682943-352x500.jpg',5)
insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'Grecja. Gorzkie pomarańcze ', 'reportaż', 'Rozszerzone wydanie książki nominowanej do nagrody im. Ryszarda Kapuścińskiego. Polski reporter z greckimi korzeniami, wychowane w Polsce trzecie dziecko Polki i Greka, szuka odpowiedzi na pytania o kryzys w kraju swoich przodków. Szuka też czegoś jeszcze – własnej tożsamości i zrozumienia powikłanej historii swojej rodziny. Dzięki tej mieszance reportażu, wspomnień i narracji podróżniczej Sturis jak żaden inny polski autor przybliża rodzimemu czytelnikowi współczesną Grecję. Ale to coś więcej niż przewodnik po kraju i jego historii. Autor "Gorzkich pomarańczy" choć częściowo obcy, w portretowaniu Grecji po mistrzowsku unika folkloru. Prezentuje ostre spojrzenie i zachowuje dystans konieczny do prawidłowego opisania zbiorowej świadomości, naszych greckich obsesji, stylu życia i trwającej dziejowej transformacji. Sturis konfrontuje się z własną przeszłością, a przed greckimi czytelnikami stawia lustro jednak nie to zaczarowane, które powie nam, że jesteśmy najpiękniejsi na świecie, a zwierciadło, które odbija naszą prawdziwą twarz - napisał o greckim wydaniu tej książki Tasos Tsakiroglou, recenzent „Efimerida ton syntakton”.', 'https://s.lubimyczytac.pl/upload/books/4880000/4880899/723812-352x500.jpg',5)
insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'Pax', 'literatura dziecięca', 'Książka nagrodzona tytułem Książki Roku 2016 lubimyczytać.pl w kategorii Literatura dziecięca. Odkąd Peter uratował osieroconego liska, on i Pax byli nierozłączni. Pewnego dnia dzieje się jednak coś, czego Peter nigdy by się nie spodziewał: jego ojciec idzie do wojska i chłopiec musi się przeprowadzić do dziadka, którego słabo zna i raczej nie lubi (ze wzajemnością) – a lisa wypuścić do lasu. Jednak już pierwszej nocy Peter wymyka się z domu dziadka i wyrusza do swojego, oddalonego o 500 kilometrów, gdzie ma nadzieję zastać Paxa. Lis w tym czasie musi się nauczyć, jak przetrwać w dzikim lesie, i na nowo odkryć świat ludzi i zwierząt. Nigdy jednak nie traci nadziei, że jego chłopiec po niego wróci. Czy dwunastolatek dotrze sam do domu? I czy odnajdzie tam Paxa?', 'https://s.lubimyczytac.pl/upload/books/3746000/3746080/507338-352x500.jpg',3)
insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'Mała księżniczka', 'literatura dziecięca', 'Siedmioletnia Sara Crewe trafia prosto z Indii do londyńskiej pensji panny Minchin. Dziewczynce rozpieszczanej przez samotnego ojca ma i tutaj nie zbywać na niczym. Traktowana jest jak księżniczka. Przełożona pensji, będąc pod wrażeniem fortuny kapitana Crewe, pozwala jej na dużo więcej niż innym uczennicom, a kiedy dowiaduje się jeszcze, że jej pupilka ma stać się w przyszłości właścicielką kopalni diamentów, jej zachwyt nie ma granic. Choć z drugiej strony nic nie potrafi zmienić niechęci, jaką w głębi serca czuje do dziewczynki. Dlatego też, gdy kopalnie okazują się mirażem, jej zawód nie ma granic i panna Minchin postanawia zamienić swoją "wzorową uczennicę" w posługaczkę i to tylko dlatego, że wyrzucenie dziewczynki na bruk mogłoby źle wyglądać. Z kolei Sara, która nawet będąc nieprawdopodobnie bogatą, zachowywała szlachetność i skromność, wykazuje niezwykły hart ducha i walczy, by w najbardziej upokarzających sytuacjach nie zatracić tego, co uważa za ważne. Wciąż stara się zdobywać wiedzę i pomagać potrzebującym. I choć czasami jest bardzo trudno, właśnie wtedy zachowuje się jak prawdziwa księżniczka.', 'https://s.lubimyczytac.pl/upload/books/4957000/4957894/880283-352x500.jpg',6)
insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'Gwiazdkowy Prosiaczek', 'literatura dziecięca', 'Jack jest bardzo przywiązany do swojej przytulanki, Daj Prosia. DP jest z nim zawsze, na dobre i na złe. Nagle w Wigilię staje się coś strasznego – DP ginie. Na szczęście Wigilia to noc cudów. W tę noc życie wstępuje nawet w zabawki. Gwiazdkowy Prosiaczek, nowa przytulanka Jacka (beznadziejny zamiennik starej), ma śmiały plan: udadzą się razem w magiczną podróż, aby odnaleźć to, co utracone, i ocalić najlepszego przyjaciela, jakiego Jack kiedykolwiek miał… „Gwiazdkowy Prosiaczek” to emocjonująca i podnosząca na duchu powieść przygodowa o więzi chłopca i jego ukochanej przytulanki oraz o tym, na jak wiele potrafi się on zdobyć, by odzyskać swój skarb. To książka jednotomowa, niepowiązana z wcześniejszymi utworami J.K. Rowling, skierowana do dzieci w wieku 8+. Tę powieść pióra autorki zaliczanej do największych pisarzy świata pokochają całe rodziny. Autorem kolorowej ilustracji okładkowej jest Jim Field, który stworzył również projekt wnętrza książki, w tym dziewięć czarno-białych ilustracji dwustronicowych i kilka mniejszych. Niektóre z nich pokażemy w ramach kampanii przedpremierowej.', 'https://s.lubimyczytac.pl/upload/books/4975000/4975498/936006-352x500.jpg',3)
insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'Tajemnica domu w Bielinach', 'literatura dziecięca', '„Tajemnica domu w Bielinach to mądra opowieść dla całej rodziny i magiczna przygoda pełna słowiańskich legend, która mnie oczarowała.” - Brandon Mull, autor serii „Baśniobór” i „Smocza Straż” W życie rodzeństwa Lipowskich – Bogusi, Leszka, Tosi i Dąbrówki – bez zaproszenia wkracza ZMIANA. Muszą zostawić rodzinną Warszawę i przeprowadzić się do Bielin. Rodzice wywrócili ich życie do góry nogami, kiedy podjęli decyzję o ROZWODZIE. Zmianom nie ma końca, bo zamieszkują w wielkim domu ciotki Mirki, w młodości szeptuchy, teraz schorowanej staruszki, która w dodatku z każdym dniem wydaje się tracić coraz bardziej kontakt z rzeczywistością, a w cichych rozmowach pojawia się słowo ALZHEIMER. Wychowane w stolicy dzieci na wsi poznają inny świat – starych wierzeń, szeptuch, bóstw i demonów. Czy to tylko SŁOWIAŃSKIE LEGENDY, czy może na własnej skórze młodzi Lipowscy przekonają się, że to nie zapomniane zabobony, ale prawda. Ciepła, zabawna, pełna magii i empatii historia, która wciągnie Wasze dzieciaki w świat słowiańskich mitów i zawładnie ich wyobraźnią. To historia, do której zechcą wracać i która będzie ich bawić i uczyć. A jeśli znacie i kochacie serię „Kwiat paproci”, to „Tajemnica domu w Bielinach” będzie również dla Was idealnym uzupełnieniem fantastycznego świata Katarzyny Bereniki Miszczuk.', 'https://s.lubimyczytac.pl/upload/books/4989000/4989465/937358-352x500.jpg',6)
insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'Ostatnie Życzenie', 'fantasy', 'Później mówiono, że człowiek ów nadszedł od północy, od Bramy Powroźniczej. Nie był stary, ale włosy miał zupełnie białe. Kiedy ściągnął płaszcz, okazało się, że na pasie za plecami ma miecz. Białowłosego przywiodło do miasta królewskie orędzie: trzy tysiące orenów nagrody za odczarowanie nękającej mieszkańców Wyzimy strzygi. Takie czasy nastały. Dawniej po lasach jeno wilki wyły, teraz namnożyło się rozmaitego paskudztwa – gdzie spojrzysz, tam upiory, bazyliszki, diaboły, żywiołaki, wiły i utopce plugawe. A i niebacznie uwolniony z amfory dżinn, potrafiący zamienić życie spokojnego miasta w koszmar, się trafi. Tu nie wystarczą zwykłe czary ani osinowe kołki. Tu trzeba zawodowca. - WIEDŹMINA - Mistrza magii i miecza. Tajemną sztuką wyuczonego, by strzec na świecie moralnej i biologicznej równowagi.', 'https://s.lubimyczytac.pl/upload/books/240000/240310/490965-352x500.jpg',7)
insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'Miecz przeznaczenia', 'fantasy', 'Wiedźmiński kodeks stawia tę sprawę w sposób jednoznaczny: wiedźminowi smoka zabijać się nie godzi. To gatunek zagrożony wymarciem. Aczkolwiek w powszechnej opinii to gad najbardziej wredny. Na oszluzgi, widłogony i latawce kodeks polować przyzwala. Ale na smoki – nie. Wiedźmin Geralt przyłącza się jednak do zorganizowanej przez króla Niedamira wyprawy na smoka, który skrył się w jaskiniach Gór Pustulskich. Na swej drodze spotyka trubadura Jaskra oraz – jakżeby inaczej – czarodziejkę Yennefer. Wśród zaproszonych przez króla co sławniejszych smokobójców jest Eyck z Denesle, rycerz bez skazy i zmazy, Rębacze z Cinfrid i szóstka krasnoludów pod komendą Yarpena Zigrina. Motywacje są różne, ale cel jeden. Smok nie ma szans.', 'https://s.lubimyczytac.pl/upload/books/240000/240312/490966-352x500.jpg',7)
insert into book (amount,title, genre, description, img,publishing_house_id) values (2,'Księga drogi i dobra', 'fantasy', 'Zachwycająca interpretacja jednej z najważniejszych ksiąg w dziejach ludzkości w mistrzowskim przekładzie Justyny Bargielskiej i Jerzego Jarniewicza. "Księga drogi i dobra" powstała prawdopodobnie dwadzieścia pięć stuleci temu. Napisał ją ktoś, kto być może nazywał się Lao Tzu, a żył mniej więcej w tym samym czasie co Konfucjusz. Nic pewnego o tej Księdze powiedzieć nie sposób, poza tym, że to dzieło chińskie, bardzo stare, i że przemawia do czytelników na całym świecie, jakby je napisano zaledwie wczoraj. Miałam wielkie szczęście, że odkryłam go w tak młodym wieku i mogłam żyć z jego księgą przez całe swoje życie. Autorzy naukowych przekładów "Księgi drogi i dobra" jako podręcznika dla władców używają słownictwa, które podkreśla wyjątkowość taoistycznego "mędrca", jego męskość, władzę. Ja zaś chciałam stworzyć wersję Księgi taką, która byłaby przystępna czytelnikom współczesnym, niewyrafinowanym, niesprawującym władzy, i być może też nie-męskim. Takim, którzy nie szukają ezoterycznych tajemnic, ale nasłuchują głosu przemawiającego wprost do duszy. Chciałabym, żeby zrozumieli, dlaczego tak bardzo kochano tę Księgę przez dwadzieścia pięć wieków. (ze wstępu Ursuli K. Le Guin) "Księga drogi i dobra" to wyjątkowa, niezwykle inspirująca poetycka interpretacja starożytnego tekstu stworzonego dwa i pół tysiąca lat temu przez twórcę taoizmu. Według jej mądrości żadna z naszych cech nie różni się od cech doskonałego władcy, genialnego myśliciela czy wielkodusznego filantropa. W tej przełomowej współczesnej interpretacji starożytnego klasyka Ursula Le Guin przedstawia uświęconą i zadziwiająco potężną filozofię chińskiego myśliciela. Książka zawiera obszerne osobiste komentarze autorki, podkreślające wartość tekstu - zwłaszcza w naszych trudnych, niespokojnych czasach. Przekładu tej mistrzowskiej interpretacji dokonali Justyna Bargielska i Jerzy Jarniewicz, a notę o autorce przetłumaczyła Kaja Gucio.', 'https://s.lubimyczytac.pl/upload/books/4943000/4943154/849840-352x500.jpg',7)
insert into book (amount,title, genre, description, img,publishing_house_id) values  (1,'Kotolotki', 'fantasy', 'Pewnego razu mieszkająca w mieście kotka urodziła czworo kociąt. I nie byłoby w tym nic dziwnego, gdyby nie fakt, że kociaki miały... skrzydła. Korzystając z rzadkiej u kotów umiejętności latania, Thelma, Harriet, James i Roger postanawiają udać się w świat, aby go lepiej poznać. Nie wiedzą jednak, że z dala od domu będą czyhać na nie różne niebezpieczeństwa. Czy znajdą bezpieczne schronienie? Czy spotkają po drodze przyjaciół? A co jeśli trafią w złe Ręce? Przepełnione urzekającym ciepłem historie autorstwa jednej z największych legend literatury fantastycznej to niezapomniana lektura dla całej rodziny. W niniejszym zbiorze znajdują się wszystkie części cyklu: "Kotolotki" , "Powrót kotolotków" , "Wspaniały Aleksander i kotolotki" , "Jane rozkłada skrzydła" / Ilustracje: S.D. Schindler', 'https://s.lubimyczytac.pl/upload/books/4924000/4924426/809072-352x500.jpg',7)
