insert into form (author,description,img,publishing_house,title,user_id) values ('Andrzej Sapkowski','"Czas pogardy" to czwarty tom sagi o Wiedźminie Geralcie. Nadchodzi zapowiadany wcześniej przez Intlinne czas miecza i topora Na powierzchnię zaczynają wyłazić złowieszcze szczury, uwielbiające czynić zło. ','https://cf-taniaksiazka.statiki.pl/images/large/A9E/9788375780666.jpg','SuperNowa','Wiedźmin. Tom 4. Czas pogardy',1)